sp_widget.client_script:
function($rootScope,$scope) {
	/* widget controller */
	var c = this;

	c.selectItem = function(index) {	
		var id =	c.data.notes[index].sys_id;
		console.log('Dear JD, Note ID je : ' + id);

		$rootScope.noteIDENTIFIKATOR = id;
		$rootScope.$emit('selectNote', id);
	}


	//The Notes List widget will listen for the event and 
	//will update the appropriate record in the list in response to the event.
	//	var updatedNoteID = c.data.noteID;
	//	$rootScope.updNOTEID = updatedNoteID;
	//	$rootScope.$emit('updateNote', updatedNoteID);

	$rootScope.$on('updateNote', function(event,data) { 

		c.server.get({		 
		}).then(function(r){ 
			//var title = r.data.title;
			//var body = r.data.note;
			//$rootScope.$emit('updateNote', title, body)

			c.data.title = r.data.title;
			c.data.note = r.data.note;

		});
	}); 

}

