sp_widget.script:
(function() {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */

	data.notes = [];

	var noteGR = new GlideRecord('x_snc_createnotes_note');	// sys_created_on

	var user1 = gs.getUser().getID();
	//var user2 = '' ; // gs.getUserID();	
	$sp.log("Dear JD, user 1 is: " + user1);
	noteGR.addQuery('user',gs.getUser().getID());
	//	Sort the records by descending order based on the sys_created_on date field value
	noteGR.orderByDesc('sys_created_on');
	//For each of the Note records returned
	noteGR.query();
	while(noteGR.next()){
		// Create an empty object, noteObj
		var noteObj = {};
		// Get the display values for the number, title, and sys_id fields and put those values into the noteObj object
		// Number, User, Titul, Note
		$sp.getRecordDisplayValues(noteObj, noteGR, 'user, number , title, note, sys_id');

		// getRecordDisplayValues(data, from: fn(tableName: string), names: string)
		// getRecordDisplayValues(): copies display values for the specified fields into the data parameter

		//get the first 20 characters of the description
		noteObj.note = noteGR.getValue('note').slice(0,20);
		data.notes.push(noteObj);
	}


})();
